import * as ImagePicker from 'expo-image-picker';
import React, { useState, useEffect } from 'react';
import { ScrollView, StyleSheet, TouchableOpacity, FlatList, Modal, Button, Image, Platform } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';


import { Text, useThemeColor, View} from '../components/Themed';
import { BackgroundImage } from 'react-native-elements/dist/config';

export default function Home({ navigation }) {
  const [modalOpen, setModalOpen] = useState(false);


  const [image, setImage] = useState(null);

  useEffect(() => {
    (async () => {
      if (Platform.OS !== 'web') {
        const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
        if (status !== 'granted') {
          alert('Sorry, we need camera roll permissions to make this work!');
        }
      }
    })();
  }, []);

  var pickAnImage = null;
  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    console.log(result);

    if (!result.cancelled) {
      setImage(result.uri);
    }
  
    if (result != null) {
      pickAnImage = "Choose another image"
    } else {
      pickAnImage = "Pick an image from camera roll"
    }
  
  };

  
 
  return (
    
    <ScrollView style={styles.container} showsVerticalScrollIndicator ={false}
    showsHorizontalScrollIndicator={false}>
<View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>

<Modal visible={modalOpen} animationType='slide'>
        <View style={styles.modalContent}>
          <MaterialIcons 
            name='close'
            size={24} 
            style={{...styles.modalToggle, ...styles.modalClose}} 
            onPress={() => setModalOpen(false)} 
          />
          <Text>Hello from the modal :)</Text>
        </View>
      </Modal>
      
      <Text style={styles.title}></Text>
      <Text style={styles.title}>ID Card</Text>
      <View style={styles.separator} lightColor="#e0e0e0" darkColor="rgba(255,255,255,0.1)" /> 
    <Button 
    title="Choose image" onPress={pickImage} />
    {image && <Image source={{ uri: image }} style={{ width: 400, height: 260, borderRadius: 20 }} />}
    <Text style={styles.information}>You can upload your identification card here. Make sure to align the left and right sides of your screen with the card, disregarding the top and bottom.</Text>
    {/* <Text onPress={() => setModalOpen(true)}>open</Text> */}

    <Text style={styles.title}></Text>
      {/* <Text style={styles.title}>Calculator</Text>
      <View style={styles.separator} lightColor="#e0e0e0" darkColor="rgba(255,255,255,0.1)" /> 
      <Text style={styles.title}></Text> */}
      <Text style={styles.title}>Equations</Text>
      <View style={styles.separator} lightColor="#e0e0e0" darkColor="rgba(255,255,255,0.1)" /> 
    </View>   
    <Image source={{uri: 'https://static.docsity.com/documents_pages/2019/04/16/b8df047399ee665f8141c5f3df0a092e.png'}} style={{width: 400, height: 500}}/>

    <Image source={{uri: 'https://schoolbag.info/physics/cracking_ap_1/images/000040.jpg'}} style={{width: 400, height: 500}}/>

    <Image source={{uri: 'https://image.slidesharecdn.com/stuffmustknowcoldnew-160417162109/95/crib-sheet-ap-calculus-ab-and-bc-exams-1-638.jpg?cb=1460910135'}} style={{width: 400, height: 500}}/>

    </ScrollView>
  );
  
}

const styles = StyleSheet.create({
  modalToggle: {
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 10,
    borderWidth: 1,
    borderColor: '#f2f2f2',
    padding: 10,
    borderRadius: 10,
    alignSelf: 'center',
  },
  modalClose: {
    marginTop: 20,
    marginBottom: 0,
  },
  modalContent: {
    flex: 1,
  },
  container: {
    flex: 1,
    //alignItems: 'center',
    // justifyContent: 'flex-start',
  },
  title: {
    fontSize: 45,
    fontWeight: '100',
    justifyContent: 'center',
    textAlign: 'center',
    paddingTop: 20
  },
  information: {
    fontSize: 15,
    fontWeight: '100',
    justifyContent: 'center',
    textAlign: 'center',
    fontStyle: 'italic',
    paddingTop: 5
  },
  body: {
    fontSize: 20,
    fontWeight: 'normal',
    flex: .1,
    justifyContent: 'center',
    textAlign: 'center'
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '100%',
    justifyContent: 'center',
    textAlign: 'center'
  },
  massagecontainer: {
    flex: 1,
    justifyContent: 'center',
    paddingTop: 44,
    padding: 8,
    backgroundColor: 'rgb(255,255,255)',
    textAlign: 'center'
  },
  massageheader: {
    fontSize: 18,
    fontWeight: 'bold',
    textAlign: 'center',
    
  },
  massageparagraph: {
    margin: 24,
    textAlign: 'center',
  },
  massageseparator: {
    marginVertical: 8,
    borderBottomColor: '#eef2f9',
    borderBottomWidth: StyleSheet.hairlineWidth,
    textAlign: 'center',
    justifyContent: 'center'
  },
  row: {
    flex: 1,
    flexDirection: "row",
  },
  inputWrap: {
    flex: 1,
    borderColor: "#eef2f9",
    // borderBottomWidth: 1,
    // marginBottom: 10
  },
  textStyle: {
    fontSize: 25,
    fontWeight: "500",
    letterSpacing: 1.5,
    fontFamily: Platform.OS == "android" ? "notoserif" : "system",
    marginTop: 5,
    // padding: 20
  },
  pedometerinfo: {
    fontSize: 27,
    fontWeight: '100'

  }
  
});
