import React, { Component, useState } from 'react';
import { Button, Platform, Vibration, SafeAreaView, StyleSheet, Switch } from 'react-native';
import { ScrollView } from 'react-native-gesture-handler';

import { Text, View} from '../components/Themed';
const ONE_SECOND_IN_MS = 1;

const PATTERN = [1 * ONE_SECOND_IN_MS, 2 * ONE_SECOND_IN_MS, 3 * ONE_SECOND_IN_MS];

const PATTERN_DESC =
    Platform.OS === 'android'
      ? 'wait 1s, vibrate 2s, wait 3s'
      : 'wait 1s, vibrate, wait 2s, vibrate, wait 3s';

      const [isEnabled, setIsEnabled] = useState(false);
      const toggleSwitch = () => setIsEnabled(previousState => !previousState);

      import Modal from 'react-native-modal';


const Separator = () => {
  return <View style={Platform.OS === 'android' ? styles.separator : null} />;
};
export default class TabFourScreen extends Component {
  state = {
    isModalVisible: false
  }

  _showModal = () => this.setState({ isModalVisible: true })

  _hideModal = () => this.setState({ isModalVisible: false })

  render () {
    return (
      // <View style={{ flex: 1 }}>
      //   <Text>Enter</Text>
      //   <Text>Enter</Text>
      //   <Text onPress={this._showModal}>Enter</Text>
      //   <TouchableOpacity>
      //     <Text>Show Modal</Text>
      //   </TouchableOpacity>

      // </View>

      <View style={styles.container}>
      <Text style={styles.title}></Text>
      <Text style={styles.title}>Health & Wellness</Text>
      <View style={styles.separator} lightColor="#eee" darkColor="rgba(255,255,255,0.1)" /> 
      <Text style={styles.body}>Pedometer</Text>      
      <Text style={styles.body} onPress={this._showModal}>Massager</Text>
      <Text style={styles.body}>Mental Health + Positive Messages</Text>

      <Text style={styles.title}></Text>
      <Text style={styles.title}>Tools</Text>
      <View style={styles.separator} lightColor="#eee" darkColor="rgba(255,255,255,0.1)" /> 
      <Text style={styles.body}>Scanner</Text>      
      <Text style={styles.body}>Calculator</Text>

      <Text style={styles.title}></Text>
      <Text style={styles.title}>General</Text>
      <View style={styles.separator} lightColor="#eee" darkColor="rgba(255,255,255,0.1)" /> 
      <Text style={styles.body}>Calendar</Text>      
      <Text style={styles.body}>Daily Reminders (Homework, Exercise)</Text>
      <Text style={styles.body}>Timer (Pomo, Regular)</Text>

      <Modal isVisible={this.state.isModalVisible} style={{backgroundColor: 'white'}}>  

          {/* <View style={styles.container}>
          <Switch
            trackColor={{ false: '#767577', true: '#81b0ff' }}
            thumbColor={isEnabled ? '#f5dd4b' : '#f4f3f4'}
            ios_backgroundColor="#3e3e3e"
            onValueChange={toggleSwitch}
            value={isEnabled}
          />
        </View> */}
          <SafeAreaView style={styles.massagecontainer}>
          <Text style={[styles.massageheader, styles.massageparagraph]}>Massager</Text>
            <Button title="Vibrate once" onPress={() => Vibration.vibrate()} />

          <Separator />
          {Platform.OS == 'android'
            ? [
                <View>
                  <Button
                    title="Vibrate for 10 seconds"
                    onPress={() => Vibration.vibrate(10 * ONE_SECOND_IN_MS)}
                  />
                </View>,
                <Separator />,
              ]
            : null}
          <Text style={styles.massageparagraph}>Pattern: {PATTERN_DESC}</Text>
          <Button title="Vibrate with pattern" onPress={() => Vibration.vibrate(PATTERN)} />
          <Separator />
          <Button
            title="Vibrate with pattern until cancelled"
            onPress={() => Vibration.vibrate(PATTERN, true)}
          />
          <Separator />
          <Button title="Stop vibration pattern" onPress={() => Vibration.cancel()} color="#FF0000" />
        </SafeAreaView>

          {/* <View style={{ flex: 1 }}>
            <Text>Must work!</Text>

              <Text onPress={this._hideModal}>Hide Modal</Text>

          </View> */}
        </Modal>
    </View>
    )
  }
}



